import React, { useState } from "react";
import {
    DropdownMenu,
    DropdownItem,
    DropdownDivider,
    Dropdown,
  } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';



const styleDropdown = {
    width : "700px",
    padding: "50px",
    borderColor: "black"
}

function PortfolioDropdown(props){
    //example
     const ls = {1:"a" , 2:"b" , 3:"c"}

    //const ls = props.list;

    let list_menu = [];

    Object.entries(ls).map(entry => {
        let label = entry[0];
        let link = entry[1];
        list_menu.push(
            <DropdownItem>
                <a href={link}>{label}</a>
            </DropdownItem>
        )
    })


    return (
            <Dropdown text=/*{props.name}*/ "example" style={styleDropdown}>
                <DropdownMenu>
                {
                    list_menu
                }       
                </DropdownMenu>
            </Dropdown>
    )
}

export default PortfolioDropdown;
