import React, { useState } from "react";

function BiodataBio(props){
    const [hover, setHover] = useState(false);

    const handleMouseEnter = () => setHover(true);

    const handleMouseLeave = () => setHover(false);

    const buttonStyle = {
      backgroundColor: hover ? "#7b42bcff" : "white",
      borderColor : hover ? "white" : "#7b42bcff",
      textColor :  hover ? "white" : "#7b42bcff",
      borderWidth : "5px",
      height : "50px",
      borderRadius : "50px",
      fontSize: "16px",
      cursor: "pointer",

    };

  return (
    <div>
        <h2 style={{ color: '#7b42bcff' , margin : "5px"}}>{props.name}</h2>
        <h1 style={{margin : "5px"}}>{props.whoami}</h1>
        <p style={{margin : "5px", width : "500px" , overflowWrap : "break-word"}}> {props.bio} </p>    
        <button
        style={buttonStyle}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        >

          <b style={{padding : "10px"}}>LinkedIn</b>

        </button>
    </div>

  )
}

export default BiodataBio;
