import logo from './logo.svg';
import { display } from '@mui/system';

//import component
import PortfolioDropdown from './component/portfolio-dropdown';
import PortfolioHighlight from './component/portfolio-highlight';

const stylePortfolio = {
  position: "relative",
  display: "flex",
  flexDirection: "column",
  justifyContent: "center",
  alignItems: "center",
  gap: "5px",
  paddingBottom: "100px",
  paddingTop: "100px"
}

const styleHighlight = {
  paddingTop : "30px",
  position: "relative",
  display: "flex",
  flexDirection: "row",
  justifyContent: "center",
  alignItems: "center",
  gap: "10px",
}

const styleHighlightItem = {

}

const styleDropdown = {
  position: "relative",
  display: "flex",
  flexDirection: "column",
  justifyContent: "center",
  alignItems: "center",
  gap: "10px",
  paddingTop : "40px"
}

const styleDropdownItem = {
  paddingBottom : "20px",
}



function Portfolio() {

  // HIGHLIGHT LIST
    /*
        HIGHLIGHT
        import LogoPrometheus from "../img/prometheus.svg";

        const logo = LogoPrometheus;
        const title = "Node Exporter, Prometheus, & Grafana";
        const link = "https://www.google.com"

        const highlight = [[logoObject, "title", link],
                          [LogoPrometheus, "Node Exporter, Prometheus, & Grafana", "https://www.google.com"]]
    */  
  // can be contain 4 - 5
  const highlight = []

  //DROPDOWN LIST


  // generate highlight portfolioHighlight
  let HIGHLIGHT_LIST = []

  highlight.forEach(i => {
    <span style={styleHighlightItem}> <PortfolioHighlight logo={i[0]} 
                                                          title={i[1]}
                                                          link={i[2]}
    /> </span>
  })


  return (
    <div style={stylePortfolio}>
      <h1 style={{margin : "5px", paddingTop : "5%"}}>Portfolio</h1>
      <div style={styleHighlight}>
        <span style={styleHighlightItem}> <PortfolioHighlight /> </span> 
        <span style={styleHighlightItem}> <PortfolioHighlight /> </span> 
        <span style={styleHighlightItem}> <PortfolioHighlight /> </span> 
        <span style={styleHighlightItem}> <PortfolioHighlight /> </span> 
      </div>
      <div style={styleDropdown}>
        <span style={styleDropdownItem}> <PortfolioDropdown /> </span> 
        <span style={styleDropdownItem}> <PortfolioDropdown /> </span> 
        <span style={styleDropdownItem}> <PortfolioDropdown /> </span> 
        <span style={styleDropdownItem}> <PortfolioDropdown /> </span> 
        <span style={styleDropdownItem}> <PortfolioDropdown /> </span> 
      </div>
    </div>
  );
}

export default Portfolio;
