import React, { useState } from "react";
import {
    DropdownMenu,
    DropdownItem,
    DropdownDivider,
    Dropdown,
  } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';



const styleDropdown = {
    width : "700px",
    padding: "5px",
    fontSize: "16px",
}

const styleLine = {
    width : "800px",
    padding: "5px",
    borderStyle:"solid",
    borderColor: "red",
    borderWidth: "2px",

    borderRadius : "20px",
    backgroundColor: "white",
    boxShadow: "0px 8px 16px 0px rgba(0,0,0,0.2)"

}

const styleItem = {
    width : "700px",
}

function PortfolioDropdown(props){
    //example
     const ls = {1:"a" , 2:"b" , 3:"c"}

    //const ls = props.list;

    let list_menu = [];

    Object.entries(ls).map(entry => {
        let label = entry[0];
        let link = entry[1];
        list_menu.push(
            <DropdownItem style={styleItem}>
                <a href={link}>{label}</a>
            </DropdownItem>
        )
    })


    return (
        <div id="line" style={styleLine}>
            <Dropdown text=/*{props.name}*/"example" 
                      style={styleDropdown}
                      icon="">
                <DropdownMenu>
                {
                    list_menu
                }       
                </DropdownMenu>
            </Dropdown>
        </div>

    )
}

export default PortfolioDropdown;
