import React, { useState } from "react";


function BiodataBio(props){

    const [isHover, setIsHover] = useState(false);

    const handleMouseEnter = () => {
        setIsHover(true);
    };
    const handleMouseLeave = () => {
        setIsHover(false);
    };

    const styleButton = {
        color: isHover ? "white" : "#7b42bcff",
        backgroundColor: isHover ? "#7b42bcff" : "white",
        borderColor: isHover ? "#7b42bcff" : "white",
        borderWidth: "5px",
        height: "40px",
        borderRadius: "20px",
        outline: "inherit"
    }
    return (
    <div>
        <h2 style={{ color: '#7b42bcff' , margin : "5px"}}>{props.name}</h2>
        <h1 style={{margin : "5px"}}>{props.whoami}</h1>
        <p style={{margin : "5px", width : "500px" , overflowWrap : "break-word"}}> {props.bio} </p>    
        <input
            type="button"
            value="LinkedIn"
            style = {styleButton}
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
        />
    </div>

    )
}

export default BiodataBio;
