import React, { useEffect } from "react";
import ReactDOM from "react-dom";
//import ReactCardCarousel from "react-card-carousel";
import SkillsetCard from "./component/skillset-card";


import PropTypes from 'prop-types';
import { Box } from "@mui/material";
import { Tab } from "@mui/material";
import { TabContext, TabList, TabPanel } from '@mui/lab';


const styleSkillset = {
  display: "flex",
  flexDirection: "column",
  justifyContent: "center",
  alignItems: "center",

  paddingTop: "100px",
  paddingBottom: "100px"
}

const styleCard = {
  height: "500px",
  paddingBottom: "50px",
  width: "40%",
  borderRadius: "20px",
  boxShadow: "0px 8px 16px 0px rgba(0,0,0,0.2)",

  display: "flex",
  flexDirection: "column",
  justifyContent: "center",
  alignItems: "center",
}

const styleCenter ={
  display: "flex",
  flexDirection: "column",
  justifyContent: "center",
  alignItems: "center",
}

function Skillset(props) {
  var settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  const [value, setValue] = React.useState('1');

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };


  // listing
  /*
      Example
          let tab_list = ["programming", "DevOps"]
          let content_list = { 
                "programming": [
                { type: 'Java', level: 85 },
                { type: 'Javascript', level: 75 },
                { type: 'Javascript', level: 75 },
                { type: 'Javascript', level: 75 },
                { type: 'Javascript', level: 75 },
              ],
                "DevOps": [
                { type: 'Java', level: 85 },
                { type: 'Javascript', level: 75 },
                { type: 'Javascript', level: 75 },
                { type: 'Javascript', level: 75 },
                { type: 'Javascript', level: 75 },
              ],
        }
  */
  return (
    <div style={styleSkillset}>
      <h1 style={{margin : "5px", paddingBottom : "50px"}}>Skillset</h1>

      <Box sx={{ width: '100%', typography: 'body1' }}>
        <TabContext value={value}>
          <Box sx={{ borderBottom: 1, borderColor: 'divider', alignItems:"center" }}>
            <TabList onChange={handleChange} aria-label="lab API tabs example" centered>
              <Tab label="Item One" value="1" />
              <Tab label="Item Two" value="2" />
              <Tab label="Item Three" value="3" />
            </TabList>
          </Box>

          <TabPanel value="1"><div style={styleCenter}><SkillsetCard /></div></TabPanel>
          <TabPanel value="2"><div style={styleCenter}><SkillsetCard /></div></TabPanel>
          <TabPanel value="3"><div style={styleCenter}><SkillsetCard /></div></TabPanel>
        </TabContext>
      </Box>
    </div>
  );
}

export default Skillset;
