import React, { useState } from "react";


function BiodataBio(props){

    const [isHover, setIsHover] = useState(false);

    const handleMouseEnter = () => {
        setIsHover(true);
    };
    const handleMouseLeave = () => {
        setIsHover(false);
    };

    const styleButton = {
        color: isHover ? "wvite" : "#7b42bcff",
        borderColor: "#7b42bcff",
        borderWidth: "1px",
        height: "40px",
        borderRadius: "20px",
    }
    return (
    <div>
        <h2 style={{ color: '#7b42bcff' , margin : "5px"}}>{props.name}</h2>
        <h1 style={{margin : "5px"}}>{props.whoami}</h1>
        <p style={{margin : "5px", width : "500px" , overflowWrap : "break-word"}}> {props.bio} </p>    
        <button
            style = {styleButton}
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
        >

            <b style={{padding : "10px"}}>LinkedIn</b>

        </button>
    </div>

    )
}

export default BiodataBio;
