import React, { useState } from "react";
import {
    DropdownMenu,
    DropdownItem,
    DropdownDivider,
    Dropdown,
  } from 'semantic-ui-react';
  import 'semantic-ui-css/semantic.min.css';

  const options = [
    { key: 1, text: 'Choice 1', value: 1 },
    { key: 2, text: 'Choice 2', value: 2 },
    { key: 3, text: 'Choice 3', value: 3 },
  ]

function PortfolioDropdown(){
    const ls = {1:"a" , 2:"b" , 3:"c"}


    return (
      <Dropdown text='File'>
        <DropdownMenu>
          <DropdownItem>Test 1</DropdownItem>
          <DropdownItem>Test 1</DropdownItem>
          <DropdownItem>Test 1</DropdownItem>
          {
            Object.entries(ls).map(entry => {
                let label = entry[0];
                let link = entry[1];
                <DropdownItem>
                    <a href={link}>{label}</a>
                </DropdownItem>
            })
          }

        </DropdownMenu>
      </Dropdown>
    )
}

export default PortfolioDropdown;
